er > 1) {                    html += '<td><button type="button" name="remove" id="" class="btn btn-danger remove">Remove</button></td></tr>';                    $('tbody').append(html);                } else {                    html += '<td><button type="button" name="add" id="add" class="btn btn-success">Add</button></td></tr>';                    $('tbody').html(html);                }            }            $(document).on('click', '#add', function () {                count++;                dynamic_field(count);            });            $(document).on('click', '.remove', function () {                count--;                $(this).closest("tr").remove();            });        });    </script>@endsection